import React, {useState} from 'react'
import {Link} from  'react-router-dom'
import PageDefault from '../../../components/PageDefault'
import FormField from '../../../components/FormField'


function CadastroCategoria(){
    const [categorias, setCategorias] = useState([]); /* Lista de categorias */
    const valoresIniciais ={
        nome:'', 
        descricao:'',
        cor:'#000',
    }

    const [values, setValues] = useState(valoresIniciais); /* valores iniciais que aparecem nos campos */

    function setValue(chave, valor){ /* função para a criação de uma categoria */
        setValues({
            ...values,
            [chave]:valor,
        })
    }

    function handleChange(event){
        setValue(
            event.target.getAttribute('name'),
            event.target.value
            );
    }
    return (
        <PageDefault>
            <h1> Cadastro Categoria: {values.nome} </h1>

            <Link to = '/'>
                Voltar para Home
            </Link>

            <form onSubmit= {function handleSubmit(event){
                event.preventDefault();
                setCategorias([
                    ...categorias,
                    values,
                ]);
                setValues(valoresIniciais)
            }}> 

              <FormField 
                label="Nome da Categoria"
                type="text"
                name="nome"
                value = {values.nome}
                onChange ={handleChange}
              />

              <FormField
                label="Descrição"
                type="textarea"
                name="descricao"
                value={values.descricao}
                onChange={handleChange}
              />
                {/* <div>
                    <label>
                                Descrição
                            <textarea 
                                name="descricao"
                                value={values.descricao}
                                onChange = {handleChange}
                            />
                    </label>
                </div> */}

                <FormField
                    label = "Cor"
                    name="cor"
                    value={values.cor}
                    onChange = {handleChange}
                />
              {/*   <div>
                    <label>
                        Cor
                        <input 
                            type="color" 
                            name="cor"
                            value={values.cor}
                            onChange = {handleChange}
                        />
                     </label>
                </div>
 */}
                <button>
                    Cadastrar
                </button>
            </form>

            <ul>
                {categorias.map((categoria, indice) => {
                    return (
                        <li key = {`${categoria}${indice}`}>
                            {categoria.nome}
                         </li>
                    )
                })}
            </ul>
        </PageDefault>
    )
}

export default CadastroCategoria
